/*** TABLES ***/
table {
	margin:0;
	padding:0;
}
tbody {
	display: table-cell;
	margin: 0;
	padding: 0;
}
th, td {
	padding: 8px 15px;
}
th {
	background:rgba(0, 0, 0, .45);
}
/*** TABLES END ***/

/*** CHRONO NAVIGATIONS ***/
.sect-wrapper {
	font-family: 'Lato', sans-serif;
}
.sect-wrapper h2 {
	font-weight: bold;
	margin-bottom: 10px;
}
.chrono-btn_container {
	display: grid;
	gap: 10px;
	grid-template-columns: repeat(10, 1fr);
	grid-template-rows: repeat(2, 1fr);
	padding: 20px 15px;
}
.chrono-btn {
	font-weight: bold;
	font-family: 'Lato', sans-serif;
	font-size: 22px;
	padding: 6px 12px;
	background: rgb(240, 240, 240);
	border-radius: 3px;
	transform: scale(1) translateY(0);
	box-shadow: 0 0 5px rgba(0, 0, 0, .25);
  color: black;
	transition: background var(--chr-btn-time) ease, transform var(--chr-btn-time) ease, color var(--chr-btn-time) ease, box-shadow var(--chr-btn-time) ease;
	text-align: center;
}
.chrono-btn:hover {
	background: var(--nv-item-hover-lt);
	box-shadow: 0 0 12px rgba(0, 0, 0, .45);
	transform: scale(1.02) translateY(-2px);
  color: white;
}

.artwork-showcase {
  height: 235px;
  width: 100%;
  border: 1px solid #aaa;
  border-radius: 6px;
  overflow: hidden;
  position: relative;
}

.artwork-stats-21,
.artwork-stats-20 {
  display: block;
  opacity: 1 !important;
  position: absolute;
  width: 100%;
  top: 0;
  left: 0;
  padding-top: 12px;
}
.artwork-stats {
  width: 100%;
  height: 200px;
  opacity: 0;
  transition: all 150ms ease;
}
.artwork-showcase_footer {
  position: absolute;
  bottom: 7px;
  padding: 0 12px;
  display: flex;
  width: 100%;
  justify-content: flex-end;
  align-items: center;
  padding-top: 8px;
}
.artwork-showcase_footer::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  display: block;
  width: 100%;
  height: 1px;
  background: #aaa;
}
.aw-1 {
  background:hsl(150, 66%, 15%);
}
.aw-2 {
  background:hsl(150, 66%, 20%);
}
.aw-3 {
  background:hsl(150, 66%, 25%);
}
.aw-4 {
  background:hsl(150, 66%, 30%);
}
.aw-box_legend {
  display: block;
  border-radius: 4px;
  width: 17px;
  height: 17px;
  margin-left: 3px;
}
.artwork-showcase_footer >*{
  margin-left: 3px;
}
.aw-ft-text {
  font-size: 90%;
  opacity: 65%;
}
